import React, { useEffect, useState } from 'react';
import { useFormik } from 'formik';
import * as Yup from 'yup';
import axios from 'axios';
import 'bootstrap/dist/css/bootstrap.min.css';

interface UserProfile {
    firstName: string;
    lastName: string;
    email: string;
}

const Profile: React.FC = () => {
    const [initialValues, setInitialValues] = useState<UserProfile>({
        firstName: "",
        lastName: "",
        email: ""
    });

    useEffect(() => {
        const fetchUserInfo = async () => {
            const token = localStorage.getItem('token');
            console.log("Token:", token); // Log token
            try {
                const response = await axios.get<UserProfile>('http://localhost:5000/api/profile', {
                    headers: { Authorization: `Bearer ${token}` },
                });
                console.log("User Data:", response.data); // Log fetched user data
                setInitialValues(response.data);
            } catch (error) {
                console.error("Error fetching user info:", error);
            }
        };

        fetchUserInfo();
    }, []);

    const formik = useFormik({
        initialValues: initialValues,
        validationSchema: Yup.object({
            firstName: Yup.string().required('First name is required'),
            lastName: Yup.string().required('Last name is required'),
            email: Yup.string().email('Invalid email address').required('Email is required'),
        }),
        enableReinitialize: true,
        onSubmit: async (values) => {
            const token = localStorage.getItem('token');
            try {
                await axios.put('http://localhost:5001/api/profile', values, {
                    headers: { Authorization: `Bearer ${token}` },
                });
                alert('Profile updated successfully!');
            } catch (error) {
                console.error("Error updating profile:", error);
            }
        },
    });

    return (
        <div className="container mt-5">
            <h1 className="text-center">User Profile</h1>
            <form onSubmit={formik.handleSubmit} className="mt-4">
                <div className="mb-3">
                    <label htmlFor="firstName" className="form-label">First Name</label>
                    <input
                        id="firstName"
                        name="firstName"
                        type="text"
                        onChange={formik.handleChange}
                        onBlur={formik.handleBlur}
                        value={formik.values.firstName}
                        className={`form-control ${formik.touched.firstName && formik.errors.firstName ? 'is-invalid' : ''}`}
                    />
                    {formik.touched.firstName && formik.errors.firstName ? (
                        <div className="invalid-feedback">{formik.errors.firstName}</div>
                    ) : null}
                </div>
                <div className="mb-3">
                    <label htmlFor="lastName" className="form-label">Last Name</label>
                    <input
                        id="lastName"
                        name="lastName"
                        type="text"
                        onChange={formik.handleChange}
                        onBlur={formik.handleBlur}
                        value={formik.values.lastName}
                        className={`form-control ${formik.touched.lastName && formik.errors.lastName ? 'is-invalid' : ''}`}
                    />
                    {formik.touched.lastName && formik.errors.lastName ? (
                        <div className="invalid-feedback">{formik.errors.lastName}</div>
                    ) : null}
                </div>
                <div className="mb-3">
                    <label htmlFor="email" className="form-label">Email</label>
                    <input
                        id="email"
                        name="email"
                        type="email"
                        onChange={formik.handleChange}
                        onBlur={formik.handleBlur}
                        value={formik.values.email}
                        className={`form-control ${formik.touched.email && formik.errors.email ? 'is-invalid' : ''}`}
                    />
                    {formik.touched.email && formik.errors.email ? (
                        <div className="invalid-feedback">{formik.errors.email}</div>
                    ) : null}
                </div>
                <button type="submit" className="btn btn-primary">Update Profile</button>
                <a href="/logout" className="btn btn-danger ms-2">Logout</a>
            </form>
        </div>
    );
};

export default Profile;
