import React, { useState } from 'react';
import axios from 'axios';
import 'bootstrap/dist/css/bootstrap.min.css';
import './Signup.css'; 
import { useNavigate } from 'react-router-dom';

interface SignupFormData {
    firstName: string;
    lastName: string;
    email: string;
    phone: string;
    gender: string;
    userType: string;
    hobbies: string[];
    profileImage: File | null;
    agency: string;
    resume: File | null;
}

const Signup: React.FC = () => {
    const navigate=useNavigate()
    const [formData, setFormData] = useState<SignupFormData>({
        firstName: '',
        lastName: '',
        email: '',
        phone: '',
        gender: '',
        userType: 'Job Seeker',
        hobbies: [],
        profileImage: null,
        resume: null,
        agency: '',
    });

    // eslint-disable-next-line @typescript-eslint/no-unused-vars
    const agencies = [
        { id: 1, name: "Agency One" },
        { id: 2, name: "Agency Two" },
        { id: 3, name: "Agency Three" },
        { id: 4, name: "Agency Four" },
        { id: 5, name: "Agency Five" }
    ];

    const handleChange = (e: React.ChangeEvent<HTMLInputElement | HTMLSelectElement>) => {
        const { name, value, type } = e.target;

        if (name === 'phone' && !/^\d*$/.test(value)) {
            return; 
        }

        if (type === 'checkbox') {
            const checked = (e.target as HTMLInputElement).checked;
            setFormData(prev => ({
                ...prev,
                hobbies: checked
                    ? [...prev.hobbies, value]
                    : prev.hobbies.filter(hobby => hobby !== value),
            }));
        } else if (type === 'file') {
            const files = (e.target as HTMLInputElement).files;
            if (name === 'profileImage' && files) {
                setFormData({ ...formData, profileImage: files[0] });
            } else if (name === 'resume' && files) {
                setFormData({ ...formData, resume: files[0] });
            }
        } else {
            setFormData({ ...formData, [name]: value });
        }
    };

    const handleSubmit = async (e: React.FormEvent<HTMLFormElement>) => {
        e.preventDefault();
        const data = new FormData();

        for (const key in formData) {
            const value = formData[key as keyof SignupFormData];
            if (Array.isArray(value)) {
                value.forEach(hobby => {
                    data.append('hobbies', hobby);
                });
            } else if (value !== null) {
                data.append(key, value);
            }
        }

        try {
            await axios.post('http://localhost:7000/api/register', data);
            alert('Signup successful! Please check your email for login details.');
            navigate('/login')
        } catch (error) {
            alert('Error signing up. Please try again.');
        }
    };

    return (
        <div className="signup-container">
            <h2 className="text-center">Job Portal</h2>
            <form onSubmit={handleSubmit} className="signup-form shadow-lg p-4 rounded">
                <div className="mb-3">
                    <input
                        name="firstName"
                        onChange={handleChange}
                        required
                        placeholder="First Name"
                        className="form-control form-input"
                    />
                </div>
                <div className="mb-3">
                    <input
                        name="lastName"
                        onChange={handleChange}
                        required
                        placeholder="Last Name"
                        className="form-control form-input"
                    />
                </div>
                <div className="mb-3">
                    <input
                        name="email"
                        type="email"
                        onChange={handleChange}
                        required
                        placeholder="Email"
                        className="form-control form-input"
                    />
                </div>
                <div className="mb-3">
                    <input
                        name="phone"
                        type="tel"
                        onChange={handleChange}
                        required
                        placeholder="Phone"
                        className="form-control form-input"
                    />
                </div>
                <div className="mb-3">
                    <label className="form-label">Gender:</label>
                    <div>
                        <label className="me-3">
                            <input type="radio" name="gender" value="Male" onChange={handleChange} required /> Male
                        </label>
                        <label>
                            <input type="radio" name="gender" value="Female" onChange={handleChange} required /> Female
                        </label>
                    </div>
                </div>
                <div className="mb-3">
                    <label className="form-label">User Type:</label>
                    <select name="userType" onChange={handleChange} className="form-select form-input">
                        <option value="Job Seeker">Job Seeker</option>
                        <option value="Agency">Agency</option>
                    </select>
                </div>
                <div className="mb-3">
                    <label className="form-label">Hobbies:</label>
                    <div>
                        <label className="me-3">
                            <input type="checkbox" value="Sports" onChange={handleChange} /> Sports
                        </label>
                        <label className="me-3">
                            <input type="checkbox" value="Dance" onChange={handleChange} /> Dance
                        </label>
                        <label className="me-3">
                            <input type="checkbox" value="Reading" onChange={handleChange} /> Reading
                        </label>
                        <label>
                            <input type="checkbox" value="Singing" onChange={handleChange} /> Singing
                        </label>
                    </div>
                </div>
                <div className="mb-3">
                    <input type="file" name="profileImage" onChange={handleChange} accept=".png, .jpg, .jpeg" className="form-control form-input" />
                </div>
                {formData.userType === 'Job Seeker' && (
                    <>
                        <div className="mb-3">
                            <input type="file" name="resume" onChange={handleChange} accept=".docx, .pdf" className="form-control form-input" />
                        </div>
                        <div className="mb-3">
                            <select name="agency" onChange={handleChange} className="form-select form-input">
                                <option value="">Select Agency</option>

                                <option value="1">Agency 1</option>
                                <option value="2">Agency 2</option>
                                <option value="3">Agency 3</option>
                            </select>
                        </div>
                    </>
                )}
                <button type="submit" className="btn btn-primary w-100">Sign Up</button>
                <div className="mt-3 text-center">
                    <a href="/login">Already have an account? Login</a>
                </div>
            </form>
        </div>
    );
};

export default Signup;
