import React from 'react';
import { useFormik } from 'formik';
import * as Yup from 'yup';
import axios from 'axios';
import { useNavigate } from 'react-router-dom';

const Signup: React.FC = () => {
  const navigate = useNavigate();

  const formik = useFormik({
    initialValues: {
      firstName: '',
      lastName: '',
      email: '',
      password: '',
      confirmPassword: '',
    },
    validationSchema: Yup.object({
      firstName: Yup.string().required('Required'),
      lastName: Yup.string().required('Required'),
      email: Yup.string().email('Invalid email format').required('Required'),
      password: Yup.string().min(6, 'Password must be at least 6 characters').required('Required'),
      confirmPassword: Yup.string()
        .oneOf([Yup.ref('password')], 'Passwords must match')
        .required('Required'),
    }),
    onSubmit: async (values) => {
      try {
        await axios.post('http://localhost:5000/api/signup', values);
        alert('Signup successful! Redirecting to login...');
        navigate('/login');
      } catch (error) {
        alert('Error during signup. Please try again.');
      }
    },
  });

  return (
    <form onSubmit={formik.handleSubmit}>
      <input name="firstName" placeholder="First Name" onChange={formik.handleChange} value={formik.values.firstName} />
      {formik.errors.firstName && <div>{formik.errors.firstName}</div>}

      <input name="lastName" placeholder="Last Name" onChange={formik.handleChange} value={formik.values.lastName} />
      {formik.errors.lastName && <div>{formik.errors.lastName}</div>}

      <input name="email" type="email" placeholder="Email" onChange={formik.handleChange} value={formik.values.email} />
      {formik.errors.email && <div>{formik.errors.email}</div>}

      <input name="password" type="password" placeholder="Password" onChange={formik.handleChange} value={formik.values.password} />
      {formik.errors.password && <div>{formik.errors.password}</div>}

      <input name="confirmPassword" type="password" placeholder="Confirm Password" onChange={formik.handleChange} value={formik.values.confirmPassword} />
      {formik.errors.confirmPassword && <div>{formik.errors.confirmPassword}</div>}

      <button type="submit">Sign Up</button>
      <p>Already have an account? <a href="/login">Login</a></p>
    </form>
  );
};

export default Signup;
