import React, { useState } from 'react';
import axios from 'axios';
import 'bootstrap/dist/css/bootstrap.min.css';
import { useNavigate } from 'react-router-dom';

const Login: React.FC = () => {
    const navigate = useNavigate();
    const [formData, setFormData] = useState<{ email: string; password: string }>({
        email: '',
        password: '',
    });
    const [error, setError] = useState<string>('');

    const handleChange = (e: React.ChangeEvent<HTMLInputElement>) => {
        setFormData({ ...formData, [e.target.name]: e.target.value });
    };

    const handleSubmit = async (e: React.FormEvent<HTMLFormElement>) => {
        e.preventDefault();
        try {
            const response = await axios.post('http://localhost:5001/api/login', formData);
            // Save the token or user info for future requests
            localStorage.setItem('token', response.data.token); // Adjust according to your API response
            alert('Login successful!');
            navigate('/dashboard'); // Redirect to the dashboard or main page
        } catch (error) {
            setError('Invalid email or password. Please try again.');
        }
    };

    return (
        <div className="container mt-5">
            <h2 className="text-center">Login</h2>
            <form onSubmit={handleSubmit} className="w-75 mx-auto bg-light p-4 rounded shadow">
                {error && <div className="alert alert-danger">{error}</div>}
                <div className="mb-3">
                    <input
                        name="email"
                        type="email"
                        onChange={handleChange}
                        required
                        placeholder="Email"
                        className="form-control"
                    />
                </div>
                <div className="mb-3">
                    <input
                        name="password"
                        type="password"
                        onChange={handleChange}
                        required
                        placeholder="Password"
                        className="form-control"
                    />
                </div>
                <button type="submit" className="btn btn-primary w-100">Login</button>
                <div className="mt-3 text-center">
                    {/* <a href="/signup">Don't have an account? Sign Up</a> */}
                </div>
            </form>
        </div>
    );
};

export default Login;
