import React from 'react';
import { useFormik } from 'formik';
import * as Yup from 'yup';
import axios from 'axios';
import 'bootstrap/dist/css/bootstrap.min.css';

// Define the shape of the form values
interface LoginFormValues {
    email: string;
    password: string;
}

const Login: React.FC = () => {
    // Initialize formik with initial values and validation schema
    const formik = useFormik<LoginFormValues>({
        initialValues: {
            email: '',
            password: '',
        },
        validationSchema: Yup.object({
            email: Yup.string()
                .email('Invalid email address')
                .required('Email is required'),
            password: Yup.string()
                .min(6, 'Password must be at least 6 characters')
                .required('Password is required'),
        }),
        onSubmit: async (values) => {
            try {
                const response = await axios.post('http://localhost:5000/api/login', values);
                localStorage.setItem('token', response.data); // Save token to localStorage
                alert('Login successful!');
                window.location.href = '/profile'; // Redirect after successful login
            } catch (error) {
                alert('Error logging in. Please try again.'); // Handle login error
            }
        },
    });

    return (
        <div className="container mt-5">
            <form onSubmit={formik.handleSubmit} className="bg-light p-4 rounded shadow">
                <h2 className="mb-4">Login</h2>
                <div className="mb-3">
                    <label htmlFor="email" className="form-label">Email</label>
                    <input
                        id="email"
                        name="email"
                        type="email"
                        onChange={formik.handleChange}
                        onBlur={formik.handleBlur}
                        value={formik.values.email}
                        required
                        placeholder="Email"
                        className={`form-control ${formik.touched.email && formik.errors.email ? 'is-invalid' : ''}`}
                    />
                    {formik.touched.email && formik.errors.email ? (
                        <div className="invalid-feedback">{formik.errors.email}</div>
                    ) : null}
                </div>
                <div className="mb-3">
                    <label htmlFor="password" className="form-label">Password</label>
                    <input
                        id="password"
                        name="password"
                        type="password"
                        onChange={formik.handleChange}
                        onBlur={formik.handleBlur}
                        value={formik.values.password}
                        required
                        placeholder="Password"
                        className={`form-control ${formik.touched.password && formik.errors.password ? 'is-invalid' : ''}`}
                    />
                    {formik.touched.password && formik.errors.password ? (
                        <div className="invalid-feedback">{formik.errors.password}</div>
                    ) : null}
                </div>
                <button type="submit" className="btn btn-primary">Login</button>
                <div className="mt-3">
                    <a href="/signup">Don't have an account? Sign Up</a>
                </div>
            </form>
        </div>
    );
};

export default Login;
