import React, { useState } from 'react';
import axios from 'axios';
import 'bootstrap/dist/css/bootstrap.min.css';

interface SignupFormData {
    firstName: string;
    lastName: string;
    email: string;
    phone: string;
    gender: string;
    userType: string;
    hobbies: string[];
    profileImage: File | null;
    resume: File | null;
    agency: string;
}

const Signup: React.FC = () => {
    const [formData, setFormData] = useState<SignupFormData>({
        firstName: '',
        lastName: '',
        email: '',
        phone: '',
        gender: '',
        userType: 'Job Seeker',
        hobbies: [],
        profileImage: null,
        resume: null,
        agency: '',
    });

    const agencies = [
        { id: 1, name: "Agency One" },
        { id: 2, name: "Agency Two" },
        { id: 3, name: "Agency Three" },
        { id: 4, name: "Agency Four" },
        { id: 5, name: "Agency Five" }
    ];

    const handleChange = (e: React.ChangeEvent<HTMLInputElement | HTMLSelectElement>) => {
        const { name, value, type } = e.target;

        if (type === 'checkbox') {
            const checked = (e.target as HTMLInputElement).checked;
            setFormData(prev => ({
                ...prev,
                hobbies: checked
                    ? [...prev.hobbies, value]
                    : prev.hobbies.filter(hobby => hobby !== value),
            }));
        } else if (type === 'file') {
            const files = (e.target as HTMLInputElement).files;
            if (name === 'profileImage' && files) {
                setFormData({ ...formData, profileImage: files[0] });
            } else if (name === 'resume' && files) {
                setFormData({ ...formData, resume: files[0] });
            }
        } else {
            setFormData({ ...formData, [name]: value });
        }
    };

    const handleSubmit = async (e: React.FormEvent<HTMLFormElement>) => {
        e.preventDefault();
        const data = new FormData();

        for (const key in formData) {
            const value = formData[key as keyof SignupFormData];
            if (Array.isArray(value)) {
                value.forEach(hobby => {
                    data.append('hobbies', hobby);
                });
            } else if (value !== null) {
                data.append(key, value);
            }
        }

        try {
            await axios.post('http://localhost:5001/api/signup', data);
            alert('Signup successful! Please check your email for login details.');
        } catch (error) {
            alert('Error signing up. Please try again.');
        }
    };

    return (
        <div className="container mt-5">
            <h2 className="text-center">User Register</h2>
            <form onSubmit={handleSubmit} className="w-75 mx-auto bg-light p-4 rounded shadow">
                {/* Other form fields */}
                {formData.userType === 'Job Seeker' && (
                    <>
                        <div className="mb-3">
                            <input
                                type="file"
                                name="resume"
                                onChange={handleChange}
                                accept=".docx, .pdf"
                                className="form-control form-input"
                            />
                        </div>
                        <div className="mb-3">
                            <select name="agency" onChange={handleChange} className="form-select form-input">
                                <option value="">Select Agency</option>
                                {agencies.map(agency => (
                                    <option key={agency.id} value={agency.name}>
                                        {agency.name}
                                    </option>
                                ))}
                            </select>
                        </div>
                    </>
                )}
                {/* Submit button and other fields */}
            </form>
        </div>
    );
};

export default Signup;
