import React, { useState } from 'react';
import axios from 'axios';
import './Signup.css';


const Signup: React.FC = () => {
    const [formData, setFormData] = useState({
        firstName: '',
        lastName: '',
        email: '',
        password: '',
        confirmPassword: ''
    });

    const handleChange = (e: React.ChangeEvent<HTMLInputElement>) => {
        setFormData({ ...formData, [e.target.name]: e.target.value });
    };

    const handleSubmit = async (e: React.FormEvent<HTMLFormElement>) => {
        e.preventDefault();
        try {
            await axios.post('/api/signup', formData);
            alert('Signup successful! Please login.');

            window.location.href = '/login';
        } catch (error) {
            alert('Error signing up. Please try again.');
        }
    };


    return (
        <form onSubmit={handleSubmit}>
            <input name="firstName" onChange={handleChange} required placeholder="First Name" /><br />
            <input name="lastName" onChange={handleChange} required placeholder="Last Name" /><br />
            <input name="email" type="email" onChange={handleChange} required placeholder="Email" /><br />
            <input name="password" type="password" onChange={handleChange} required placeholder="Password" /><br />
            <input name="confirmPassword" type="password" onChange={handleChange} required placeholder="Confirm Password" /><br />
            <button type="submit">Sign Up</button><br />
            <a href="/login">Already have an account? Login</a>
        </form>
    );
};

export default Signup;
