import React, { useState } from 'react';
import axios from 'axios';
import 'bootstrap/dist/css/bootstrap.min.css'; // Import Bootstrap CSS
import './Signup.css'; // Custom CSS (if needed)

const Signup: React.FC = () => {
    const [formData, setFormData] = useState({
        firstName: '',
        lastName: '',
        email: '',
        phone: '',
        gender: '',
        userType: '',
        hobbies: [] as string[],
        profileImage: null as File | null,
        agencyId: '',
        resume: null as File | null,
    });

    const agencies = ['Agency A', 'Agency B', 'Agency C'];

    const handleChange = (e: React.ChangeEvent<HTMLInputElement | HTMLSelectElement>) => {
        const { name, value, type } = e.target;

        if (type === 'file') {
            const target = e.target as HTMLInputElement;
            const file = target.files ? target.files[0] : null;
            setFormData({ ...formData, [name]: file });
        } else if (name === 'hobbies') {
            const updatedHobbies = formData.hobbies.includes(value)
                ? formData.hobbies.filter(hobby => hobby !== value)
                : [...formData.hobbies, value];
            setFormData({ ...formData, hobbies: updatedHobbies });
        } else {
            setFormData({ ...formData, [name]: value });
        }
    };

    const handleSubmit = async (e: React.FormEvent) => {
        e.preventDefault();

        const formPayload = new FormData();
        for (const key in formData) {
            const value = formData[key as keyof typeof formData];

            if (value instanceof File) {
                formPayload.append(key, value);
            } else if (Array.isArray(value)) {
                value.forEach(hobby => formPayload.append('hobbies[]', hobby));
            } else if (value) {
                formPayload.append(key, value);
            }
        }

        try {
            const response = await axios.post('http://localhost:5000/api/users/register', formPayload, {
                headers: {
                    'Content-Type': 'multipart/form-data',
                },
            });
            console.log(response.data);
        } catch (error) {
            console.error('Error registering user:', error);
        }
    };

    return (
        <div className="container mt-5">
            <form onSubmit={handleSubmit} className="bg-light p-4 rounded shadow">
                <h1 className="text-center mb-4">Registration Form</h1>
                <div className="mb-3">
                    <input
                        type="text"
                        name="firstName"
                        placeholder="First Name"
                        onChange={handleChange}
                        required
                        className="form-control"
                    />
                </div>
                <div className="mb-3">
                    <input
                        type="text"
                        name="lastName"
                        placeholder="Last Name"
                        onChange={handleChange}
                        required
                        className="form-control"
                    />
                </div>
                <div className="mb-3">
                    <input
                        type="email"
                        name="email"
                        placeholder="Email"
                        onChange={handleChange}
                        required
                        className="form-control"
                    />
                </div>
                <div className="mb-3">
                    <input
                        type="tel"
                        name="phone"
                        placeholder="Phone"
                        onChange={handleChange}
                        required
                        pattern="[0-9]*"
                        className="form-control"
                    />
                </div>

                <div className="mb-3">
                    <label className="me-2">Gender:</label>
                    <div className="form-check form-check-inline">
                        <input
                            type="radio"
                            name="gender"
                            value="Male"
                            onChange={handleChange}
                            required
                            className="form-check-input"
                        />
                        <label className="form-check-label">Male</label>
                    </div>
                    <div className="form-check form-check-inline">
                        <input
                            type="radio"
                            name="gender"
                            value="Female"
                            onChange={handleChange}
                            required
                            className="form-check-input"
                        />
                        <label className="form-check-label">Female</label>
                    </div>
                </div>

                <div className="mb-3">
                    <select name="userType" onChange={handleChange} required className="form-select">
                        <option value="">Select User Type</option>
                        <option value="Job Seeker">Job Seeker</option>
                        <option value="Agency">Agency</option>
                    </select>
                </div>

                <fieldset className="mb-3">
                    <legend>Select Hobbies:</legend>
                    {['Sports', 'Dance', 'Reading', 'Singing'].map(hobby => (
                        <div className="form-check" key={hobby}>
                            <input
                                type="checkbox"
                                name="hobbies"
                                value={hobby}
                                onChange={handleChange}
                                className="form-check-input"
                            />
                            <label className="form-check-label">{hobby}</label>
                        </div>
                    ))}
                </fieldset>

                <div className="mb-3">
                    <label>Profile Photo</label>
                    <input
                        type="file"
                        name="profileImage"
                        accept=".png, .jpg, .jpeg"
                        onChange={handleChange}
                        className="form-control"
                    />
                </div>

                {formData.userType === 'Job Seeker' && (
                    <>
                        <div className="mb-3">
                            <label>Resume</label>
                            <input
                                type="file"
                                name="resume"
                                accept=".pdf, .docx"
                                onChange={handleChange}
                                className="form-control"
                            />
                        </div>
                        <div className="mb-3">
                            <select name="agencyId" onChange={handleChange} required className="form-select">
                                <option value="">Select Agency</option>
                                {agencies.map((agency, index) => (
                                    <option key={index} value={agency}>{agency}</option>
                                ))}
                            </select>
                        </div>
                    </>
                )}

                <button type="submit" className="btn btn-primary w-100">Register</button>
            </form>
        </div>
    );
};

export default Signup;
